    //     <h4> {product.title} </h4>
    //     <img alt="" src={product.imageUrl} className="product-image" />
    //     <p>rating: {product.rating} stars</p>
    //     <p> Price: {product.price}$ </p>
    //     <button onClick={() => this.addToCart(product)}> Add to order </button>
    //   </div>
    // ));
    return <Modal isOpen={this.props.isVisible} onRequestClose={this.handleCloseModal} className="Modal" overlayClassName="Overlay">
        <button onClick={() => this.props.closeCart()}>close</button>
      </Modal>;
  }
}

const mapStateToProps = (state) => {
  return {
    cartTotalItems: state.cartReducer.totalCartItems,
    cart: state.cartReducer.cart,
    cartPrice: state.cartReducer.totalCartPrice,
    isVisible: state.cartReducer.showCart
  };
};

const mapDispatchToProps = (dispatch) => {
  return { addProductToCart: (product) => {
      dispatch(cartActions.addToCart(product));
    }, 
    closeCart: () => {
      dispatch(cartActions.showCart());
    } };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CartModalComponent);
