import React from "react";
import { connect } from "react-redux";
import "./Menu.css";
import Modal from "react-modal";
import { cartActions } from "../reducers/cart";

class MenuComponent extends React.Component {
  constructor(props) {
    super(props);
  }

  addToCart(product) {
    console.log(product, "added to cart");
    this.setState({
      selectedProduct: product,
      showModel: true,
    });
  }

  handleCloseModal(didFinishOrder) {
    if (!this.state.chosenAmount) {
      this.setState({ showAmountError: true });
    } else {
      if (didFinishOrder) {
        this.props.addProductToCart({
          ...this.state.selectedProduct,
          amount: this.state.chosenAmount,
        });
      }
      this.setState({
        showModel: false,
        chosenAmount: null,
        selectedProduct: null,
        showAmountError: false,
      });
    }
  }

  onChangeAmount(amount) {
    this.setState({
      chosenAmount: amount.value,
      showAmountError: false,
    });
  }

  render() {
    return (
      <div className="menu">
        <div className="item">
          <p>Cart: {this.props.cartTotalItems}</p>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    products: state.productReducer.products,
    cartTotalItems: state.cartReducer.totalCartItems,
    cart: state.cartReducer.cart,
    cartPrice: state.cartReducer.totalCartPrice,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addProductToCart: () => {
      dispatch(cartActions.addToCart(product));
    },
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(MenuComponent);
