import { createReducer, createActions } from "reduxsauce";
import { v4 as uuidv4 } from "uuid";
// *************** types and action creators

const { Types, Creators } = createActions({
  showOrders: null,
  getOrders: null, 
  ordersSuccess: ['orders'], 
  ordersFailure: ['error']
});

export const ordersTypes = Types;
export const ordersActions = Creators;

// *************** initial state

export const INITIAL_STATE = {
  // error: false,
  // orders: [],
  isLoading: false,

  error: false,
  isVisible: false,
  orders: [
    {
      id: uuidv4(),
      products: [
        {
          id: "12133238758-6d2a-4099-ac92-91b600f6048a",
          title: "shirt",
          price: 40,
          rating: 1,
          imageUrl:
            "https://contents.mediadecathlon.com/p1484240/k$ab565f3675dbdd7e3c486175e2c16583/men-s-travel-trekking-shirt-travel100-warm-bordeaux.jpg?&f=800x800",
          amount: 3,
        },
        {
          id: "18338758-6d2a-4099-ac92-91b600f6048a",
          title: "shirt",
          price: 40,
          rating: 1,
          imageUrl:
            "https://contents.mediadecathlon.com/p1484240/k$ab565f3675dbdd7e3c486175e2c16583/men-s-travel-trekking-shirt-travel100-warm-bordeaux.jpg?&f=800x800",
          amount: 3,
        },
      ],
      totalCartPrice: 240,
      date: Date(),
      wasNotified: true,
    },
    {
      id: uuidv4(),
      products: [
        {
          id: "12133238758-6d2a-4099-ac92-91b600f6048a",
          title: "shirt",
          price: 40,
          rating: 1,
          imageUrl:
            "https://contents.mediadecathlon.com/p1484240/k$ab565f3675dbdd7e3c486175e2c16583/men-s-travel-trekking-shirt-travel100-warm-bordeaux.jpg?&f=800x800",
          amount: 3,
        },
        {
          id: "18338758-6d2a-4099-ac92-91b600f6048a",
          title: "shirt",
          price: 40,
          rating: 1,
          imageUrl:
            "https://contents.mediadecathlon.com/p1484240/k$ab565f3675dbdd7e3c486175e2c16583/men-s-travel-trekking-shirt-travel100-warm-bordeaux.jpg?&f=800x800",
          amount: 3,
        },
      ],
      totalCartPrice: 240,
      date: Date(),
      wasNotified: false,
    },
  ],
};

// *************** reducers

export const getOrders = (state = INITIAL_STATE, action) => {
  console.log('get', action);
  return { ...state, orders: [], isLoading: true, error: false };
};

export const ordersSuccess = (state = INITIAL_STATE, action) => {
  console.log('good', action);
  return { ...state, orders: action.payload.orders, isLoading: false, error: false };
};

export const ordersFailure = (state = INITIAL_STATE, action) => {
  console.log('bad', action);
  return { ...state, orders: [], isLoading: false, error: true };
};

export const showOrders = (state = INITIAL_STATE, action) => {
  console.log(state);
  return ({ ...state, isVisible: !state.isVisible, error: false });
}


// *************** Handlers

export const HANDLERS = {
  [Types.SHOW_ORDERS]: showOrders,
  [Types.GET_ORDERS]: getOrders,
  [Types.ORDERS_SUCCESS]: ordersSuccess,
  [Types.ORDERS_FAILURE]: ordersFailure,
};

export default createReducer(INITIAL_STATE, HANDLERS);
