import React from "react";
import { connect } from "react-redux";
import Dropdown from "react-dropdown";
import "react-dropdown/style.css";
import Modal from "react-modal";
import { ordersActions } from "../reducers/orders";
import "./styles/OrdersModal.css";

class OrderModalComponent extends React.Component {
  constructor(props) {
    super(props);
  }

  removeFromCart(productId){
    this.props.removeProdct(productId)
  }

  renderCartItems() {
    // if (this.props.cart.length == 0) {
        // return (<p>Your cart is empty... </p>)
    }
    // return(
    //     this.props.cart.map(cartItem => (
    //       <div key={cartItem.id} className="item">
    //           <img src={cartItem.imageUrl} />
    //           <div className="details">
    //               <p>{cartItem.title}</p>
    //               <p>Price: {cartItem.price}$</p>
    //               <p>Amount: {cartItem.amount}</p>
    //               <button onClick={() => this.removeFromCart(cartItem.id)}>Remove!</button>
    //           </div>
    //       </div>  
    //     ))
    // )
  // }

  render() {
    return (
      <Modal
        isOpen={this.props.isVisible}
        onRequestClose={this.handleCloseModal}
        ariaHideApp={false}
        className="cart-modal"
        overlayClassName="cart-Overlay"
      >
        <h2>Your Orders</h2>
        <div className="cart-content">
            {this.renderCartItems()}
        </div>
        <div className="buttons-wrapper">
          <button onClick={() => this.props.closeCart()}>Close</button>
          <button disabled={this.props.cart.length === 0} onClick={() => this.props.closeCart()}>
          Buy for {this.props.cartPrice}$
          </button>
        </div>
      </Modal>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    isVisible: state.ordersReducer.isVisible,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    closeModal: () => {
      dispatch(ordersActions.showOrders());
    },
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(OrderModalComponent);
