import { delay, map } from 'rxjs/operators';

export const ordersEpic = action$ =>
action$.ofType("UPDATE_COUNTER")
  .pipe(
   delay(1000), // Asynchronously wait 1000ms then continue
   map((action) => (
    {
     type: "UPDATE_COMPLETE",
     payload: action.payload
    })
  ))
