import React from 'react';
import { connect } from 'react-redux';
import './ProductList.css'
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

class ProductListComponent extends React.Component {

    constructor (props) {
        super(props)
        console.log("list ", props);


        this.addToOrder = this.addToOrder.bind(this)
    }

    addToOrder(product){
        console.log(product, "added to cart");
    }

    render() {
        const productsList = this.props.products.map(product => 
            (<div key={product.id} className="product-item-wrapper">
                <h4>{product.title}</h4>
                <img alt="" src={product.imageUrl} className="product-image"/>
                <p>rating: {product.rating} stars</p>
                <p>Price: {product.price}$</p>
                <div>How Much
                <Dropdown
                placeholder="Select"
                fluid
                selection
                options={Array.from({length: 10}, (_, i) => i + 1)}
              /></div>    
                <button onClick={() => this.addToOrder(product)}>Add to order</button>
                </div>));
      return (
          <div className="product-list">
            {productsList}
          </div>

      )
  }
}

const mapStateToProps = (state) => {
    return {
      products: state.productReducer.products
    }
  }

const mapDispatchToProps = (dispatch) => {
    return {

    }
}



export default connect(mapStateToProps,mapDispatchToProps)(ProductListComponent)
