import React from 'react';
import { connect } from 'react-redux';
import './ProductList.css'
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import Modal from 'react-modal';

class ProductListComponent extends React.Component {

        constructor(props) {
            super(props)
            console.log("list ", props);
            this.state = {
                selectedProduct: null,
                showModel: true,
                chosenAmount: null,
            }
            this.addToCart = this.addToCart.bind(this)
            this.handleCloseModal = this.handleCloseModal.bind(this)
        }

        addToCart(product) {
            console.log(product, "added to cart");
            this.setState({
                selectedProduct: product,
                showModel: true
            })
        }

        handleCloseModal() {
            this.setState({
                showModel: false,
                chosenAmount: null,
                selectedProduct: null,
            })

        }

        render() {
                const productsList = this.props.products.map(product =>
                    ( < div key = { product.id }
                        className = "product-item-wrapper" >
                        <
                        h4 > { product.title } < /h4> <
                        img alt = ""
                        src = { product.imageUrl }
                        className = "product-image" / >
                        <
                        p > rating: { product.rating }
                        stars < /p> <
                        p > Price: { product.price }
                        $ < /p> <
                        button onClick = {
                            () => this.addToCart(product) } > Add to order < /button> <
                        /div>));
                        return ( <
                            div className = "product-list" > { productsList } <
                            Modal isOpen = { this.state.showModel }
                            onRequestClose = { this.handleCloseModal }
                            className = "Modal"
                            overlayClassName = "Overlay" >
                            <
                            p > How Many ? ? ? < /p> <
                            Dropdown options = { Array.from({ length: 5 }, (v, k) => k + 1) }
                            onChange = { this.onChangeAmount }
                            value = { 0 }
                            placeholder = "Choose..." / > ; <
                            button onClick = { this.handleCloseModal } > Close Modal < /button> <
                            /Modal> <
                            /div>

                        )
                    }
                }

                const mapStateToProps = (state) => {
                    return {
                        products: state.productReducer.products
                    }
                }

                const mapDispatchToProps = (dispatch) => {
                    return {

                    }
                }



                export default connect(mapStateToProps, mapDispatchToProps)(ProductListComponent)
