// sampleReducer.js
import { createReducer, createActions } from 'reduxsauce'


// *************** types and action creators

const { Types, Creators } = createActions({
    addToCart: ['product', 'amount'],
    processOrder: ['products'],
    // orderSuccess: {},
    // orderFailure: ['error']
})

export const ordersActions = Creators;

// *************** initial state

export const INITIAL_STATE = {
    error: false,
    cart: [],
    totalCartPrice: 0, 
    previousOrders: []
}

// *************** reducers

export const processOrder = (state = INITIAL_STATE, action) => {
    console.log(action);
    return {...state, error: false }
}

export const addToCart = (state = INITIAL_STATE, action) => {
    let {product} = action
    console.log(product);
    let newPrice = state.cart.totalPrice + product.price * product.amount
    return {...state, error: false }
}

// *************** Handlers

export const HANDLERS = {
    [Types.PROCESS_ORDER]: processOrder,
    [Types.ADD_TO_CART]: addToCart
}


export default createReducer(INITIAL_STATE, HANDLERS)
