import React from "react";
import { connect } from "react-redux";
import Dropdown from "react-dropdown";
import "react-dropdown/style.css";
import Modal from "react-modal";
import { cartActions } from "../reducers/cart";
import "./styles/CartModal.css";

class CartModalComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      //   selectedProduct: null,
      //   showModel: false,
      //   chosenAmount: null,
      //   showAmountError: false,
    };
    this.removeFromCart = this.removeFromCart.bind(this);
    // this.handleCloseModal = this.handleCloseModal.bind(this);
    // this.onChangeAmount = this.onChangeAmount.bind(this);
  }

  removeFromCart(productId){
    this.props.removeProdct(productId)
  }

  renderCartItems() {
    console.log();
    if (this.props.cart.length == 0) {
        return (<p>Your cart is empty... </p>)
    }
    return(
        this.props.cart.map(cartItem => (
          <div className="item">
              <img src={cartItem.imageUrl} />
              <div className="details">
                  <p>{cartItem.title}</p>
                  <p>Price: {cartItem.amount * cartItem.price}$</p>
                  <p>Amount: {cartItem.amount}</p>
                  <button onClick={() => this.removeFromCart(cartItem.id)}>Remove!</button>
              </div>
          </div>  
        ))
    )
  }

  render() {
    // const productsList = this.props.products.map((product) => (
    //   <div key={product.id} className="product-item-wrapper">
    //     <h4> {product.title} </h4>
    //     <img alt="" src={product.imageUrl} className="product-image" />
    //     <p>rating: {product.rating} stars</p>
    //     <p> Price: {product.price}$ </p>
    //     <button onClick={() => this.addToCart(product)}> Add to order </button>
    //   </div>
    // ));
    return (
      <Modal
        isOpen={this.props.isVisible}
        onRequestClose={this.handleCloseModal}
        ariaHideApp={false}
        className="cart-modal"
        overlayClassName="cart-Overlay"
      >
        <h2>Your Cart</h2>
        <div className="cart-content">
            {this.renderCartItems()}
        </div>
        <div className="buttons-wrapper">
          <button onClick={() => this.props.closeCart()}>close</button>
          <button disabled={this.props.cart.length === 0} onClick={() => this.props.closeCart()}>Submit Order</button>
        </div>
      </Modal>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    cartTotalItems: state.cartReducer.totalCartItems,
    cart: state.cartReducer.cart,
    cartPrice: state.cartReducer.totalCartPrice,
    isVisible: state.cartReducer.showCart,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    removeProdct: (productId) => {
      dispatch(cartActions.removeFromCart(productId));
    },
    closeCart: () => {
      dispatch(cartActions.showCart());
    },
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CartModalComponent);
