import {UPDATE_COUNTER, UPDATE_COMPLETE} from '../actions/counterActions'

const initialState = 0;

export const counterReducer = (state = initialState, action) => {
    switch (action.type) {
      case UPDATE_COUNTER:
        return state;

      case UPDATE_COMPLETE:
        return state + action.payload;

      default:
        return state;
    }
  };
